INSERT INTO `product` (`id`, `author`, `code`, `created_date`, `deleted`, `description`, `form`, `image_banner`, `instruction_size`, `name`, `num_page`, `nxb`, `old_price`, `price`, `publish_year`, `quantity`, `quantity_sold`, `size`, `weight`, `category_id`) VALUES
(1, 'Eric Carle', 'TVHC5012', '2024-07-19 11:54:00', b'0', '<p><strong>C&acirc;u chuyện về c&aacute;c lo&agrave;i vật ch&igrave;m v&agrave;o giấc ngủ đi k&egrave;m một chiếc chăn xinh xắn.</strong></p>\n<p><strong>The Very Hungry Caterpillar Book and Snuggle Blanket&nbsp;</strong>gồm một cuốn s&aacute;ch kể về giờ ngủ của c&aacute;c con vật với h&igrave;nh ảnh minh hoạ đặc trưng đến từ t&aacute;c phẩm &ldquo;Ch&uacute; s&acirc;u h&aacute;u ăn&rdquo; nổi tiếng, đi k&egrave;m một chiếc chăn nhỏ mềm mại.</p>\n<p><img src=\"https://thebookland.vn/thumbnail/1656474042301_z3528085810408_1c7bed927f061ec9c01a8c223b308a97.jpg\">​</p>\n<p>Trong tựa s&aacute;ch &ldquo;Sleep Tight, Very Hungry Caterpillar&rdquo;, b&eacute; sẽ gửi lời ch&uacute;c ngủ ngon đến c&aacute;c bạn dế, c&uacute; v&agrave; vịt v&agrave; xem xem ai đang cuộn tr&ograve;n như một con bọ ở trang cuối c&ugrave;ng? S&aacute;ch kể c&acirc;u chuyện nhẹ nh&agrave;ng trước giờ ngủ, với văn bản gieo vần bắt tai v&agrave; h&igrave;nh ảnh minh hoạ m&agrave;u sắc sinh động.</p>\n<p><img src=\"https://thebookland.vn/thumbnail/1656474042302_z3528085931257_f1f28ec8b05fa4cc0bca021732bfdb36.jpg\">​</p>\n<p>Chiếc chăn đi k&egrave;m c&oacute; thiết kế m&agrave;u xanh l&aacute; đặc trưng, với một đầu th&uacute; b&ocirc;ng nhỏ mềm mại m&ocirc; tả ch&uacute; s&acirc;u h&aacute;u ăn. Chăn c&oacute; k&iacute;ch thước đắp vừa với trẻ sơ sinh v&agrave; trẻ nhỏ.&nbsp;<strong>The Very Hungry Caterpillar Book and Snuggle Blanket</strong> đem đến sự ấm c&uacute;ng v&agrave; c&acirc;u chuyện th&uacute; vị để kể b&eacute; nghe trước giờ đi ngủ, nu&ocirc;i dưỡng t&igrave;nh y&ecirc;u đọc s&aacute;ch của b&eacute; từ nhỏ v&agrave; đưa b&eacute; v&agrave;o mộng đẹp.</p>', 'Bìa mềm', 'http://res.cloudinary.com/dxqh3xpza/image/upload/v1721364810/d6ubvqep5klhiiaysu7h.jpg', NULL, 'THE VERY HUNGRY CATERPILLAR BOOK AND SNUGGLE BLANKET', 20, 'Penguin Random House Children\'s UK', 450000, 375000, '2022', 130, 0, '167 x 320 x 47mm', '50', 2);

